package dayOne;

public class MathOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y, sum, diff, prod, div, mod;
		x = 70;
		y = 30;
		sum = x + y;
		diff = x - y;
		prod = x * y;
		div = x / y;
		mod = x % y;
		System.out.println("Sum of x and y is: " + sum);
		System.out.println("Difference of x and y is: " + diff);
		System.out.println("product of x and y is: " + prod);
		System.out.println("Division of x and y is: " + div);
		System.out.println("Modulus of x and y is: " + mod);
	}

}
